    "**/*.test.ts",
    "**/*.spec.ts",
    "node_modules",
    "client",
    "build",
    "dist"
  ],
  "verbose": false,
  "watch": ["./"],
  "env": {
    "NODE_ENV": "development"
  }
}

{
  "version": 2,
  "builds": [
    {
      "src": "index.js",
      "use": "@vercel/node"
    }
  ],
  "routes": [
    {
      "src": "/(.*)",
      "dest": "/"
    }
  ]
}
